/* ==========================================
   SCHOOL-THEMED AUTH PAGES - 2026
   Natural African Design + Educational Aesthetic
   ========================================== */

:root {
    /* Natural African Earth Tones */
    --primary-earth: #8B6F47;        /* Rich brown - natural, trustworthy */
    --secondary-gold: #D4A574;       /* Warm gold - academic prestige */
    --accent-teal: #2B7A78;          /* Deep teal - growth, education */
    --accent-terracotta: #C85A17;    /* Terracotta - warmth, creativity */
    --success: #52B788;              /* Natural green */
    --danger: #D62828;               /* Alert red */
    --text-dark: #2D2D2D;            /* Deep charcoal */
    --text-light: #5F5F5F;           /* Warm gray */
    --text-muted: #8B8B8B;           /* Muted gray */
    --bg-white: #FAFAF8;             /* Warm white */
    --bg-cream: #F5F1E8;             /* Cream background */
    --border-color: #D9CFBF;         /* Warm border */

    --transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
    --shadow-subtle: 0 2px 8px rgba(139, 111, 71, 0.08);
    --shadow-medium: 0 8px 24px rgba(139, 111, 71, 0.12);
}

/* ==========================================
   GLOBAL RESET
   ========================================== */

body.auth-page {
    margin: 0;
    padding: 0;
    font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Segoe UI', -apple-system, sans-serif;
    background: linear-gradient(135deg, #F5F1E8 0%, #E8DCC8 50%, #D4A574 100%);
    min-height: 100vh;
    display: flex;
    align-items: center;
    justify-content: center;
    color: var(--text-dark);
    overflow-x: hidden;
}

/* Hide navbar and footer on auth pages */
body.auth-page .navbar,
body.auth-page .site-footer {
    display: none !important;
}

/* ==========================================
   BACKGROUND ANIMATIONS - Natural Elements
   ========================================== */

body.auth-page::before {
    content: '';
    position: fixed;
    top: -10%;
    right: -5%;
    width: 500px;
    height: 500px;
    background: radial-gradient(circle, rgba(212, 165, 116, 0.2) 0%, rgba(212, 165, 116, 0) 70%);
    border-radius: 50%;
    z-index: -1;
    animation: float 12s ease-in-out infinite;
    pointer-events: none;
}

body.auth-page::after {
    content: '';
    position: fixed;
    bottom: 5%;
    left: -5%;
    width: 450px;
    height: 450px;
    background: radial-gradient(circle, rgba(43, 122, 120, 0.12) 0%, rgba(43, 122, 120, 0) 70%);
    border-radius: 50%;
    z-index: -1;
    animation: float 15s ease-in-out infinite reverse;
    pointer-events: none;
}

@keyframes float {
    0%, 100% { transform: translateY(0) scale(1); }
    50% { transform: translateY(30px) scale(1.05); }
}

@keyframes slideInUp {
    from {
        opacity: 0;
        transform: translateY(30px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

@keyframes slideInDown {
    from {
        opacity: 0;
        transform: translateY(-30px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

@keyframes pulse {
    0%, 100% { opacity: 1; }
    50% { opacity: 0.5; }
}

@keyframes spin {
    to { transform: rotate(360deg); }
}

/* ==========================================
   MAIN CONTAINER - Single Column School Layout
   ========================================== */

.auth-container {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    width: 100%;
    max-width: 500px;
    padding: 2rem 1.5rem;
    position: relative;
    z-index: 1;
}

/* ==========================================
   HERO SECTION - Compact Top Header
   ========================================== */

.auth-hero {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    text-align: center;
    color: var(--primary-earth);
    animation: slideInDown 0.6s ease-out;
    margin-bottom: 2rem;
    width: 100%;
}

.hero-content {
    max-width: 100%;
}

.brand-logo {
    font-size: 3rem;
    margin-bottom: 0.75rem;
    display: inline-block;
    animation: slideInDown 0.6s ease-out 0.1s both;
    filter: drop-shadow(0 2px 4px rgba(139, 111, 71, 0.1));
}

.brand-title {
    font-size: 2rem;
    font-weight: 700;
    margin: 0 0 0.5rem 0;
    letter-spacing: -0.5px;
    animation: slideInDown 0.6s ease-out 0.15s both;
    color: var(--primary-earth);
}

.brand-subtitle {
    font-size: 0.95rem;
    opacity: 0.8;
    line-height: 1.5;
    margin: 0;
    animation: slideInDown 0.6s ease-out 0.2s both;
    color: var(--text-light);
}

.hero-features {
    display: none;
}

/* ==========================================
   FORM SECTION - School Card Design
   ========================================== */

.auth-form-wrapper {
    display: flex;
    align-items: center;
    justify-content: center;
    width: 100%;
    animation: slideInUp 0.8s ease-out;
}

.glass-card {
    background: var(--bg-white);
    border: 1px solid var(--border-color);
    border-radius: 16px;
    box-shadow: var(--shadow-medium);
    padding: 2.5rem 2rem;
    width: 100%;
    position: relative;
    overflow: hidden;
}

.glass-card::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 4px;
    background: linear-gradient(90deg, var(--primary-earth), var(--accent-teal));
}

.card-header {
    text-align: center;
    margin: 0 0 1.75rem 0;
    padding: 0;
}

.card-header h2 {
    color: var(--primary-earth);
    font-size: 1.6rem;
    font-weight: 700;
    margin: 0 0 0.3rem 0;
}

.card-header p {
    color: var(--text-light);
    font-size: 0.9rem;
    margin: 0;
}

/* ==========================================
   FORM ELEMENTS
   ========================================== */

.login-form {
    width: 100%;
}

.auth-input-group {
    position: relative;
    margin-bottom: 1.5rem;
    display: flex;
    flex-direction: column;
}

.auth-input-group input,
.auth-input-group select {
    width: 100%;
    padding: 14px 16px;
    font-size: 1rem;
    border: 2px solid var(--border-color);
    border-radius: 10px;
    background: var(--bg-cream);
    color: var(--text-dark);
    transition: var(--transition);
    font-family: inherit;
    appearance: none;
    box-sizing: border-box;
    font-weight: 500;
    min-height: 50px;
}

/* Add extra padding on right for password toggle button */
.auth-input-group input[type="password"] {
    padding-right: 48px;
}

.auth-input-group input:focus,
.auth-input-group select:focus {
    outline: none;
    border-color: var(--primary-earth);
    box-shadow: 0 0 0 3px rgba(139, 111, 71, 0.1);
    background: var(--bg-white);
}

.auth-input-group input::placeholder {
    color: var(--text-muted);
    font-weight: 400;
}

.auth-input-group label {
    display: block;
    margin-bottom: 0.65rem;
    font-size: 0.95rem;
    font-weight: 600;
    color: var(--text-dark);
    transition: var(--transition);
}

.auth-input-group.has-error input {
    border-color: var(--danger);
}

.auth-input-group.has-error input:focus {
    box-shadow: 0 0 0 4px rgba(239, 68, 68, 0.1);
}

.auth-input-group .help-text {
    font-size: 0.8rem;
    color: var(--text-muted);
    margin-top: 0.3rem;
}

.auth-input-group .error-text {
    font-size: 0.8rem;
    color: var(--danger);
    margin-top: 0.3rem;
    font-weight: 500;
}

.toggle-password {
    position: absolute;
    right: 12px;
    top: 50%;
    transform: translateY(-50%);
    background: none;
    border: none;
    cursor: pointer;
    color: var(--text-muted);
    font-size: 1.3rem;
    padding: 8px 10px;
    margin: 0;
    transition: var(--transition);
    display: flex;
    align-items: center;
    justify-content: center;
    z-index: 10;
}

.toggle-password:hover {
    color: var(--primary-earth);
}

/* ==========================================
   BUTTONS - Large, Clear, School Themed
   ========================================== */

.btn-primary {
    width: 100%;
    padding: 16px 24px;
    background: linear-gradient(135deg, var(--primary-earth), var(--accent-teal));
    color: white;
    border: none;
    border-radius: 10px;
    font-size: 1.05rem;
    font-weight: 700;
    cursor: pointer;
    transition: var(--transition);
    margin-top: 1rem;
    box-shadow: 0 4px 12px rgba(139, 111, 71, 0.25);
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 0.5rem;
    letter-spacing: 0.3px;
}

.btn-primary:hover {
    transform: translateY(-3px);
    box-shadow: 0 8px 20px rgba(139, 111, 71, 0.35);
    background: linear-gradient(135deg, var(--primary-earth), var(--primary-earth));
}

.btn-primary:active {
    transform: translateY(-1px);
}

.btn-primary:disabled {
    opacity: 0.6;
    cursor: not-allowed;
    transform: none;
}

.btn-google,
.btn-social {
    display: flex;
    align-items: center;
    justify-content: center;
    width: 100%;
    padding: 12px 24px;
    background: var(--bg-white);
    border: 2px solid var(--border-color);
    border-radius: 10px;
    color: var(--text-dark);
    font-weight: 600;
    font-size: 0.95rem;
    gap: 0.75rem;
    cursor: pointer;
    transition: var(--transition);
    text-decoration: none;
}

.btn-google:hover,
.btn-social:hover {
    background: var(--bg-light);
    border-color: var(--primary);
    color: var(--primary);
    transform: translateY(-1px);
}

.google-icon {
    width: 20px;
    height: 20px;
}

/* ==========================================
   FORM ACTIONS & LINKS
   ========================================== */

.form-actions {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 2rem;
    font-size: 0.9rem;
}

.form-check {
    display: flex;
    align-items: center;
    gap: 0.6rem;
}

.form-check-input {
    width: 18px;
    height: 18px;
    cursor: pointer;
    accent-color: var(--primary-earth);
}

.form-check-label {
    cursor: pointer;
    color: var(--text-dark);
    font-weight: 500;
}

.form-actions a {
    color: var(--primary-earth);
    text-decoration: none;
    font-weight: 600;
    transition: var(--transition);
}

.form-actions a:hover {
    color: var(--accent-teal);
    text-decoration: underline;
}

/* ==========================================
   DIVIDERS
   ========================================== */

.social-divider {
    display: flex;
    align-items: center;
    gap: 1rem;
    margin: 2rem 0 1.5rem;
    color: var(--text-muted);
    font-size: 0.85rem;
}

.social-divider::before,
.social-divider::after {
    content: '';
    flex: 1;
    height: 1px;
    background: var(--border-color);
}
.form-actions a:hover {
    color: var(--accent-teal);
    text-decoration: underline;
}

/* ==========================================
