/* ==========================================
   RESPONSIVE DESIGN - Mobile First
   ========================================== */

@media (max-width: 768px) {
    .auth-container {
        max-width: 100%;
        padding: 1.5rem 1rem;
    }

    .glass-card {
        padding: 2rem 1.5rem;
    }

