/* ========================================
   DARK MODE - 2026 PREMIUM
   ======================================== */

:root.dark-mode {
    /* PRIMARY BRAND - Adjusted for dark */
    --purple-50: #3b3056;
    --purple-100: #4a3f6b;
    --purple-200: #5d5180;
    --purple-300: #7366a8;
    --purple-400: #8f7cc9;
    --purple-500: #a855f7;
    --purple-600: #c084fc;
    --purple-700: #d8b4fe;
    --purple-800: #e9d5ff;
    --purple-900: #f3e8ff;

    /* SECONDARY - Teal Adjusted */
    --teal-50: #05332e;
    --teal-100: #0a4d47;
    --teal-200: #0f766e;
    --teal-300: #115e59;
    --teal-400: #2dd4bf;
    --teal-500: #14b8a6;
    --teal-600: #0d9488;
    --teal-700: #5eead4;
    --teal-800: #99f6e4;
    --teal-900: #ccfbf1;

    /* NEUTRALS - Dark Mode */
    --gray-0: #0f172a;
    --gray-50: #1e293b;
    --gray-100: #334155;
    --gray-200: #475569;
    --gray-300: #64748b;
    --gray-400: #94a3b8;
    --gray-500: #cbd5e1;
    --gray-600: #e2e8f0;
    --gray-700: #f1f5f9;
    --gray-800: #f8fafc;
    --gray-900: #ffffff;

    /* TEXT - Inverted */
    --text-primary: #f8fafc;
    --text-secondary: #cbd5e1;
    --text-tertiary: #94a3b8;
    --text-inverse: #111827;

    /* GLASS EFFECT - Dark Mode */
    --glass-light: rgba(30, 41, 59, 0.7);
    --glass-dark: rgba(15, 23, 42, 0.8);

    color-scheme: dark;
}

/* ========================================
   DARK MODE - BODY & GLOBAL
   ======================================== */

body.dark-mode {
    background: linear-gradient(135deg, #0f172a 0%, #1e293b 100%);
    color: var(--text-primary);
}

body.dark-mode::before {
    background: radial-gradient(circle, rgba(168, 85, 247, 0.1) 0%, transparent 70%);
}

body.dark-mode::after {
    background: radial-gradient(circle, rgba(20, 184, 166, 0.08) 0%, transparent 70%);
}

/* ========================================
   DARK MODE - NAVBAR
   ======================================== */

.dark-mode .navbar {
    background: linear-gradient(180deg, rgba(30, 41, 59, 0.95) 0%, rgba(30, 41, 59, 0.92) 100%);
    border-bottom-color: rgba(255, 255, 255, 0.08);
    box-shadow: 0 8px 32px rgba(0, 0, 0, 0.3);
}

.dark-mode .navbar-brand {
    background: var(--gradient-premium);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
}

.dark-mode .navbar .nav-link {
    color: var(--text-secondary) !important;
}

.dark-mode .navbar .nav-link:hover {
    color: var(--purple-400) !important;
}

.dark-mode .navbar .dropdown-menu {
    background: rgba(30, 41, 59, 0.95);
    border-color: rgba(255, 255, 255, 0.08);
    box-shadow: 0 20px 25px -5px rgba(0, 0, 0, 0.3);
}

.dark-mode .navbar .dropdown-item {
    color: var(--text-secondary);
}

.dark-mode .navbar .dropdown-item:hover {
    background: rgba(168, 85, 247, 0.15);
    color: var(--purple-400);
}

.dark-mode .theme-toggle {
    background: linear-gradient(135deg, rgba(255, 255, 255, 0.1) 0%, rgba(255, 255, 255, 0.05) 100%);
    border: 1px solid rgba(255, 255, 255, 0.1);
    color: var(--text-primary);
}

.dark-mode .theme-toggle:hover {
    background: linear-gradient(135deg, rgba(255, 255, 255, 0.15) 0%, rgba(255, 255, 255, 0.1) 100%);
}

/* ========================================
   DARK MODE - CARDS
   ======================================== */

.dark-mode .card,
.dark-mode .glass-card {
    background: rgba(30, 41, 59, 0.7);
    backdrop-filter: var(--glass-blur);
    border: 1px solid rgba(168, 85, 247, 0.2);
    box-shadow: 0 8px 32px rgba(0, 0, 0, 0.3),
                inset 0 1px 1px rgba(255, 255, 255, 0.1);
}

.dark-mode .card-header {
    background: linear-gradient(135deg, rgba(168, 85, 247, 0.15) 0%, rgba(20, 184, 166, 0.1) 100%);
    border-bottom-color: rgba(255, 255, 255, 0.08);
}

.dark-mode .card-title {
    color: var(--text-primary);
}

.dark-mode .card-text {
    color: var(--text-secondary);
}

/* ========================================
   DARK MODE - STAT CARDS
   ======================================== */

.dark-mode .stat-card {
    background: rgba(30, 41, 59, 0.7);
    border-color: rgba(168, 85, 247, 0.2);
    box-shadow: 0 8px 32px rgba(0, 0, 0, 0.3),
                inset 0 1px 1px rgba(255, 255, 255, 0.1);
}

.dark-mode .stat-card::before {
    background: radial-gradient(circle, rgba(168, 85, 247, 0.15) 0%, transparent 70%);
}

.dark-mode .stat-label {
    color: var(--text-tertiary);
}

.dark-mode .stat-value {
    background: var(--gradient-premium);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
}

/* ========================================
   DARK MODE - BUTTONS
   ======================================== */

.dark-mode .btn-primary,
.dark-mode .btn-premium {
    background: linear-gradient(135deg, var(--purple-500) 0%, var(--teal-500) 100%);
    box-shadow: 0 4px 15px rgba(168, 85, 247, 0.4);
}

.dark-mode .btn-primary:hover,
.dark-mode .btn-premium:hover {
    box-shadow: 0 6px 25px rgba(168, 85, 247, 0.5);
}

.dark-mode .btn-secondary,
.dark-mode .btn-outline {
    background: transparent;
    border: 2px solid var(--purple-400);
    color: var(--purple-400);
}

.dark-mode .btn-secondary:hover,
.dark-mode .btn-outline:hover {
    background: rgba(168, 85, 247, 0.15);
    border-color: var(--purple-300);
}

/* ========================================
   DARK MODE - FORMS
   ======================================== */

.dark-mode .form-control,
.dark-mode .form-select {
    background: rgba(30, 41, 59, 0.7);
    border: 1.5px solid rgba(168, 85, 247, 0.2);
    color: var(--text-primary);
}

.dark-mode .form-control::placeholder {
    color: var(--text-tertiary);
}

.dark-mode .form-control:focus,
.dark-mode .form-select:focus {
    background: rgba(30, 41, 59, 0.9);
    border-color: var(--purple-400);
    box-shadow: 0 0 0 3px rgba(168, 85, 247, 0.2),
                inset 0 1px 2px rgba(0, 0, 0, 0.2);
}

.dark-mode .form-label {
    color: var(--text-primary);
}

.dark-mode .form-floating > label {
    color: var(--text-tertiary);
}

.dark-mode .form-floating > .form-control:focus ~ label,
.dark-mode .form-floating > .form-control:not(:placeholder-shown) ~ label {
    color: var(--purple-400);
}

.dark-mode .form-control.is-invalid {
    background: rgba(239, 68, 68, 0.08);
}

.dark-mode .form-control.is-valid {
    background: rgba(16, 185, 129, 0.08);
}

/* ========================================
   DARK MODE - ALERTS
   ======================================== */

.dark-mode .alert {
    background: rgba(30, 41, 59, 0.7);
    backdrop-filter: var(--glass-blur);
    border-left: 4px solid;
}

.dark-mode .alert-success {
    border-left-color: var(--success);
    background: rgba(16, 185, 129, 0.12);
    color: #6ee7b7;
}

.dark-mode .alert-danger {
    border-left-color: var(--danger);
    background: rgba(239, 68, 68, 0.12);
    color: #fca5a5;
}

.dark-mode .alert-warning {
    border-left-color: var(--warning);
    background: rgba(245, 158, 11, 0.12);
    color: #fcd34d;
}

.dark-mode .alert-info {
    border-left-color: var(--info);
    background: rgba(59, 130, 246, 0.12);
    color: #93c5fd;
}

/* ========================================
   DARK MODE - SKELETON
   ======================================== */

.dark-mode .skeleton {
    background: linear-gradient(
        90deg,
        rgba(255, 255, 255, 0.05) 0%,
        rgba(255, 255, 255, 0.1) 50%,
        rgba(255, 255, 255, 0.05) 100%
    );
    animation: shimmer 2s infinite;
}

/* ========================================
   DARK MODE - LINKS & TEXT
   ======================================== */

.dark-mode a {
    color: var(--purple-400);
}

.dark-mode a:hover {
    color: var(--purple-300);
}

.dark-mode .text-muted {
    color: var(--text-tertiary) !important;
}

.dark-mode .text-primary {
    color: var(--purple-400) !important;
}

/* ========================================
   DARK MODE - FOOTER
   ======================================== */

.dark-mode .site-footer {
    background: rgba(30, 41, 59, 0.5);
    border-top-color: rgba(255, 255, 255, 0.08);
}

.dark-mode .site-footer h5 {
    color: var(--text-primary);
}

.dark-mode .site-footer a {
    color: var(--text-tertiary);
}

.dark-mode .site-footer a:hover {
    color: var(--purple-400);
}

/* ========================================
   DARK MODE - TABLE
   ======================================== */

.dark-mode .table {
    color: var(--text-primary);
    border-color: rgba(255, 255, 255, 0.1);
}

.dark-mode .table-striped > tbody > tr:nth-of-type(odd) {
    background-color: rgba(30, 41, 59, 0.5);
}

.dark-mode .table thead th {
    background: rgba(30, 41, 59, 0.7);
    border-color: rgba(255, 255, 255, 0.1);
    color: var(--text-primary);
}

/* ========================================
   DARK MODE - TRANSITIONS
   ======================================== */

@media (prefers-color-scheme: dark) {
    :root {
        color-scheme: dark;
    }

    body {
        background: linear-gradient(135deg, #0f172a 0%, #1e293b 100%);
        color: var(--text-primary);
    }
}

