/* ========================================
   PREMIUM 2026 DESIGN SYSTEM - COMPLETE
   Glassmorphism + Neumorphism + Micro-interactions
   ======================================== */

/* ========================================
   1. ANIMATIONS
   ======================================== */

@keyframes fadeIn {
    from { opacity: 0; }
    to { opacity: 1; }
}

@keyframes slideInUp {
    from {
        opacity: 0;
        transform: translateY(20px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

@keyframes slideInDown {
    from {
        opacity: 0;
        transform: translateY(-20px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

@keyframes scaleIn {
    from {
        opacity: 0;
        transform: scale(0.95);
    }
    to {
        opacity: 1;
        transform: scale(1);
    }
}

@keyframes pulse {
    0%, 100% { opacity: 1; }
    50% { opacity: 0.5; }
}

@keyframes shimmer {
    0% { background-position: -1000px 0; }
    100% { background-position: 1000px 0; }
}

@keyframes float {
    0%, 100% { transform: translateY(0px); }
    50% { transform: translateY(-10px); }
}

@keyframes ripple {
    0% {
        transform: scale(0);
        opacity: 1;
    }
    100% {
        transform: scale(4);
        opacity: 0;
    }
}

/* ========================================
   2. NAVBAR - PREMIUM DESIGN
   ======================================== */

.navbar {
    background: linear-gradient(180deg, rgba(255,255,255,0.95) 0%, rgba(255,255,255,0.92) 100%);
    backdrop-filter: blur(10px);
    border-bottom: 1px solid rgba(0, 0, 0, 0.05);
    box-shadow: 0 8px 32px rgba(0, 0, 0, 0.08);
    padding: 0.75rem 0;
    z-index: 1030;
    position: sticky;
    top: 0;
}

.navbar-brand {
    font-weight: 700;
    font-size: 1.4rem;
    background: var(--gradient-premium);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    transition: var(--transition-base);
    margin-left: 1rem;
}

.navbar-brand:hover {
    transform: scale(1.05) translateZ(0);
}

.navbar .nav-link {
    color: var(--text-secondary) !important;
    font-weight: 500;
    font-size: 0.95rem;
    margin: 0 0.75rem;
    position: relative;
    transition: var(--transition-base);
}

.navbar .nav-link::after {
    content: '';
    position: absolute;
    bottom: -5px;
    left: 0;
    width: 0;
    height: 2px;
    background: var(--gradient-premium);
    transition: width var(--transition-base);
}

.navbar .nav-link:hover {
    color: var(--purple-600) !important;
}

.navbar .nav-link:hover::after {
    width: 100%;
}

.navbar .dropdown-menu {
    border: 1px solid rgba(0, 0, 0, 0.08);
    background: rgba(255, 255, 255, 0.95);
    backdrop-filter: blur(10px);
    border-radius: var(--radius-lg);
    box-shadow: var(--shadow-xl);
    animation: slideInDown var(--transition-base);
}

.navbar .dropdown-item {
    transition: var(--transition-fast);
    padding: 0.75rem 1rem;
    color: var(--text-secondary);
}

.navbar .dropdown-item:hover {
    background: linear-gradient(135deg, var(--purple-50) 0%, var(--teal-50) 100%);
    color: var(--purple-600);
    transform: translateX(4px);
}

/* Theme Toggle */
.theme-toggle {
    background: linear-gradient(135deg, var(--gray-100) 0%, var(--gray-50) 100%);
    border: none;
    border-radius: var(--radius-full);
    padding: 0.5rem 0.75rem;
    cursor: pointer;
    transition: var(--transition-base);
    font-size: 1.1rem;
    display: flex;
    align-items: center;
    justify-content: center;
    margin-left: 1rem;
}

.theme-toggle:hover {
    background: linear-gradient(135deg, var(--gray-200) 0%, var(--gray-100) 100%);
    transform: scale(1.1) rotate(-20deg);
}

/* ========================================
   3. PREMIUM STAT CARDS
   ======================================== */

.stat-card {
    background: var(--glass-light);
    backdrop-filter: var(--glass-blur);
    border: 1px solid rgba(255, 255, 255, 0.5);
    border-radius: var(--radius-lg);
    padding: 2rem;
    position: relative;
    overflow: hidden;
    transition: var(--transition-base);
    display: flex;
    align-items: center;
    gap: 1.5rem;
    box-shadow: 0 8px 32px rgba(0, 0, 0, 0.08),
                inset 0 1px 1px rgba(255, 255, 255, 0.6);
}

.stat-card::before {
    content: '';
    position: absolute;
    top: 0;
    right: 0;
    width: 150px;
    height: 150px;
    background: radial-gradient(circle, rgba(168, 85, 247, 0.1) 0%, transparent 70%);
    border-radius: 50%;
    transform: translate(30%, -30%);
    z-index: 0;
}

.stat-card:hover {
    transform: translateY(-4px);
    box-shadow: 0 12px 48px rgba(0, 0, 0, 0.12),
                inset 0 1px 1px rgba(255, 255, 255, 0.6);
}

.stat-icon {
    font-size: 3rem;
    flex-shrink: 0;
    opacity: 0.9;
    position: relative;
    z-index: 1;
}

.stat-content {
    flex-grow: 1;
    position: relative;
    z-index: 1;
}

.stat-label {
    font-size: 0.875rem;
    color: var(--text-tertiary);
    font-weight: 500;
    text-transform: uppercase;
    letter-spacing: 0.05em;
    margin-bottom: 0.5rem;
}

.stat-value {
    font-size: 2rem;
    font-weight: 700;
    background: var(--gradient-premium);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    line-height: 1;
    margin-bottom: 0.5rem;
}

.stat-trend {
    font-size: 0.875rem;
    font-weight: 600;
    display: flex;
    align-items: center;
    gap: 0.5rem;
}

.stat-trend.trend-up {
    color: var(--success);
}

.stat-trend.trend-down {
    color: var(--danger);
}

.stat-trend.trend-neutral {
    color: var(--text-tertiary);
}

/* ========================================
   4. PREMIUM BUTTONS - WITH RIPPLE EFFECT
   ======================================== */

.btn-premium,
.btn-primary {
    position: relative;
    overflow: hidden;
    border: none;
    border-radius: var(--radius-lg);
    font-weight: 600;
    font-size: 0.95rem;
    padding: 0.875rem 2rem;
    letter-spacing: -0.01em;
    transition: all var(--transition-base);
    cursor: pointer;
    box-shadow: 0 4px 15px rgba(168, 85, 247, 0.3);
}

.btn-premium:hover,
.btn-primary:hover {
    transform: translateY(-2px);
    box-shadow: 0 6px 25px rgba(168, 85, 247, 0.4);
}

.btn-premium:active,
.btn-primary:active {
    transform: translateY(0);
}

/* Ripple Effect */
.btn-premium::before,
.btn-primary::before {
    content: '';
    position: absolute;
    top: 50%;
    left: 50%;
    width: 0;
    height: 0;
    border-radius: 50%;
    background: rgba(255, 255, 255, 0.4);
    transform: translate(-50%, -50%);
    transition: width var(--transition-fast), height var(--transition-fast);
    pointer-events: none;
}

.btn-premium:active::before,
.btn-primary:active::before {
    width: 300px;
    height: 300px;
}

/* Button Variants */
.btn-secondary,
.btn-outline {
    background: transparent;
    border: 2px solid var(--purple-300);
    color: var(--purple-600);
    transition: all var(--transition-base);
}

.btn-secondary:hover,
.btn-outline:hover {
    background: var(--purple-50);
    border-color: var(--purple-500);
    transform: translateY(-2px);
}

.btn-icon {
    border-radius: var(--radius-full);
    padding: 0.75rem;
    width: 2.5rem;
    height: 2.5rem;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 1.1rem;
}

/* ========================================
   5. PREMIUM FORM COMPONENTS
   ======================================== */

.form-group {
    margin-bottom: 1.5rem;
    position: relative;
}

.form-control,
.form-select {
    background: var(--glass-light);
    backdrop-filter: var(--glass-blur);
    border: 1.5px solid var(--gray-200);
    border-radius: var(--radius-md);
    padding: 0.875rem 1rem;
    font-size: 0.95rem;
    color: var(--text-primary);
    transition: all var(--transition-base);
    font-family: var(--font-family-base);
}

.form-control::placeholder {
    color: var(--text-tertiary);
}

.form-control:focus,
.form-select:focus {
    border-color: var(--purple-500);
    background: rgba(255, 255, 255, 0.9);
    box-shadow: 0 0 0 3px rgba(168, 85, 247, 0.1),
                inset 0 1px 2px rgba(0, 0, 0, 0.05);
    outline: none;
}

.form-label {
    font-weight: 600;
    color: var(--text-primary);
    margin-bottom: 0.75rem;
    font-size: 0.95rem;
}

/* Floating Labels */
.form-floating > label {
    color: var(--text-tertiary);
}

.form-floating > .form-control:focus ~ label,
.form-floating > .form-control:not(:placeholder-shown) ~ label {
    color: var(--purple-600);
}

/* Input Validation */
.form-control.is-invalid {
    border-color: var(--danger);
    background: rgba(239, 68, 68, 0.05);
}

.form-control.is-valid {
    border-color: var(--success);
    background: rgba(16, 185, 129, 0.05);
}

.invalid-feedback,
.valid-feedback {
    font-size: 0.85rem;
    margin-top: 0.5rem;
    display: block;
    animation: slideInUp var(--transition-base);
}

.invalid-feedback {
    color: var(--danger);
}

.valid-feedback {
    color: var(--success);
}

/* Form Hint */
.form-hint {
    font-size: 0.85rem;
    color: var(--text-tertiary);
    display: block;
    margin-top: 0.5rem;
}

/* ========================================
   6. PREMIUM CARDS
   ======================================== */

.card,
.glass-card {
    background: var(--glass-light);
    backdrop-filter: var(--glass-blur);
    border: 1px solid rgba(255, 255, 255, 0.5);
    border-radius: var(--radius-lg);
    box-shadow: 0 8px 32px rgba(0, 0, 0, 0.08),
                inset 0 1px 1px rgba(255, 255, 255, 0.6);
    overflow: hidden;
    transition: all var(--transition-base);
}

.card:hover,
.glass-card:hover {
    transform: translateY(-4px);
    box-shadow: 0 12px 48px rgba(0, 0, 0, 0.12),
                inset 0 1px 1px rgba(255, 255, 255, 0.6);
}

.card-header,
.glass-card > .card-header {
    background: linear-gradient(135deg, var(--purple-50) 0%, var(--teal-50) 100%);
    border-bottom: 1px solid rgba(0, 0, 0, 0.05);
    padding: 1.5rem;
}

.card-body {
    padding: 2rem;
}

.card-title {
    font-weight: 700;
    color: var(--text-primary);
    margin-bottom: 1rem;
    font-size: 1.25rem;
}

.card-text {
    color: var(--text-secondary);
    line-height: 1.6;
}

/* ========================================
   7. ALERTS & NOTIFICATIONS
   ======================================== */

.alert {
    border-radius: var(--radius-lg);
    border-left: 4px solid;
    padding: 1rem 1.5rem;
    margin-bottom: 1.5rem;
    display: flex;
    gap: 1rem;
    align-items: flex-start;
    animation: slideInDown var(--transition-base);
    background: var(--glass-light);
    backdrop-filter: var(--glass-blur);
}

.alert-success {
    border-left-color: var(--success);
    background: rgba(16, 185, 129, 0.08);
    color: #065f46;
}

.alert-danger {
    border-left-color: var(--danger);
    background: rgba(239, 68, 68, 0.08);
    color: #7f1d1d;
}

.alert-warning {
    border-left-color: var(--warning);
    background: rgba(245, 158, 11, 0.08);
    color: #78350f;
}

.alert-info {
    border-left-color: var(--info);
    background: rgba(59, 130, 246, 0.08);
    color: #082f49;
}

.alert-icon {
    font-size: 1.25rem;
    flex-shrink: 0;
}

.alert-close {
    background: none;
    border: none;
    cursor: pointer;
    color: currentColor;
    opacity: 0.7;
    transition: var(--transition-fast);
    margin-left: auto;
}

.alert-close:hover {
    opacity: 1;
}

/* ========================================
   8. LOADING SKELETONS
   ======================================== */

.skeleton {
    background: linear-gradient(
        90deg,
        var(--gray-200) 0%,
        var(--gray-100) 50%,
        var(--gray-200) 100%
    );
    background-size: 200% 100%;
    animation: shimmer 2s infinite;
    border-radius: var(--radius-md);
}

.skeleton-text {
    height: 1rem;
    width: 100%;
    margin-bottom: 0.5rem;
}

.skeleton-avatar {
    width: 3rem;
    height: 3rem;
    border-radius: 50%;
}

/* ========================================
   9. UTILITY CLASSES
   ======================================== */

.glass {
    background: var(--glass-light);
    backdrop-filter: var(--glass-blur);
    border: 1px solid rgba(255, 255, 255, 0.5);
}

.neumorphic {
    background: linear-gradient(135deg, #ffffff 0%, #f9fafb 100%);
    box-shadow: 9px 9px 16px #b8b9be,
                -9px -9px 16px #ffffff;
}

.gradient-text {
    background: var(--gradient-premium);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
}

.shadow-hover {
    transition: box-shadow var(--transition-base);
}

.shadow-hover:hover {
    box-shadow: var(--shadow-xl);
}

.scale-hover {
    transition: transform var(--transition-base);
}

.scale-hover:hover {
    transform: scale(1.05);
}

.fade-in {
    animation: fadeIn var(--transition-base);
}

.slide-in-up {
    animation: slideInUp var(--transition-base);
}

/* ========================================
   10. RIPPLE EFFECT FOR BUTTONS
   ======================================== */

.ripple {
    position: absolute;
    border-radius: 50%;
    background: rgba(255, 255, 255, 0.5);
    transform: scale(0);
    animation: ripple-animation 0.6s ease-out;
    pointer-events: none;
}

@keyframes ripple-animation {
    to {
        transform: scale(4);
        opacity: 0;
    }
}

/* ========================================
   11. BADGE STYLES
   ======================================== */

.badge {
    padding: 0.5rem 0.75rem;
    border-radius: var(--radius-full);
    font-weight: 600;
    font-size: 0.8rem;
    letter-spacing: 0.5px;
    transition: all var(--transition-base);
}

.badge:hover {
    transform: scale(1.1);
}

.badge-primary,
.badge-purple {
    background: linear-gradient(135deg, var(--purple-500), var(--purple-600));
    color: white;
}

.badge-success {
    background: linear-gradient(135deg, var(--success), #059669);
    color: white;
}

.badge-warning {
    background: linear-gradient(135deg, var(--warning), #d97706);
    color: white;
}

.badge-danger {
    background: linear-gradient(135deg, var(--danger), #991b1b);
    color: white;
}

/* ========================================
   12. PROGRESS BAR PREMIUM
   ======================================== */

.progress {
    background: linear-gradient(90deg, var(--gray-100), var(--gray-50));
    border-radius: var(--radius-full);
    overflow: hidden;
    box-shadow: inset 0 2px 4px rgba(0, 0, 0, 0.06);
}

.progress-bar {
    background: linear-gradient(90deg, var(--purple-500), var(--teal-500));
    transition: width var(--transition-slow);
    box-shadow: 0 2px 8px rgba(168, 85, 247, 0.3);
}

/* ========================================
   13. ACTIVITY FEED
   ======================================== */

.activity-item {
    animation: slideInUp var(--transition-base);
}

/* ========================================
   14. RESPONSIVE ANIMATIONS
   ======================================== */

@media (max-width: 768px) {
    .stat-card {
        flex-direction: column;
        text-align: center;
        gap: 1rem;
    }

    .stat-icon {
        font-size: 2.5rem;
    }

    .navbar-brand {
        font-size: 1.2rem;
    }

    .btn-premium,
    .btn-primary {
        padding: 0.75rem 1.5rem;
        font-size: 0.9rem;
    }
}

@media (max-width: 576px) {
    .card-body {
        padding: 1.5rem;
    }

    .card-header {
        padding: 1.25rem;
    }

    .stat-value {
        font-size: 1.5rem;
    }
}

/* Reduce motion support */
@media (prefers-reduced-motion: reduce) {
    * {
        animation-duration: 0.01ms !important;
        animation-iteration-count: 1 !important;
        transition-duration: 0.01ms !important;
    }
}

