{% extends "base.html" %}
{% load i18n %}
{% load static %}

{% block title %}{% trans "Sign In" %}{% endblock %}

{% block body_class %}auth-page{% endblock %}
{% block main_class %}p-0{% endblock %}
{% block container_class %}p-0{% endblock %}

{% block extra_css %}
    <link rel="stylesheet" href="{% static 'css/auth.css' %}">
{% endblock %}

{% block content %}
<div class="auth-container">
    <div class="auth-hero">
        <div class="hero-content">
            <h1 class="brand-title">{% trans "Connect" %}</h1>
            <p class="brand-subtitle">{% trans "Seamlessly access your MunTech dashboard using your external account." %}</p>
        </div>
    </div>

    <div class="auth-form-wrapper">
        <div class="glass-card">
            <div class="card-header">
                <h2>{% trans "Confirm Sign In" %}</h2>
                <p>
                    {% blocktrans with provider.name as provider %}You are about to connect a new third party account from {{ provider }}.{% endblocktrans %}
                </p>
            </div>

            <form method="post" class="login-form">
                {% csrf_token %}

                <div class="alert alert-info" role="alert" style="background: rgba(99, 102, 241, 0.1); border: 1px solid rgba(99, 102, 241, 0.3); color: #6366f1; border-radius: 10px; padding: 12px 16px; margin-bottom: 1.5rem; font-size: 0.9rem;">
                    <svg style="width: 16px; height: 16px; margin-right: 8px; vertical-align: middle; display: inline-block;" viewBox="0 0 24 24" fill="currentColor">
                        <path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm0 18c-4.41 0-8-3.59-8-8s3.59-8 8-8 8 3.59 8 8-3.59 8-8 8zm3.5-9c.83 0 1.5-.67 1.5-1.5S16.33 8 15.5 8 14 8.67 14 9.5s.67 1.5 1.5 1.5zm-7 0c.83 0 1.5-.67 1.5-1.5S9.33 8 8.5 8 7 8.67 7 9.5 7.67 11 8.5 11zm3.5 6.5c2.33 0 4.31-1.46 5.11-3.5H6.89c.8 2.04 2.78 3.5 5.11 3.5z"/>
                    </svg>
                    {% trans "Please confirm you want to continue." %}
                </div>

                <button type="submit" class="btn-primary" style="text-transform: uppercase; font-weight: 700; letter-spacing: 0.5px;">
                    {% trans "Continue" %}
                </button>

                <a href="{% url 'account_login' %}" class="btn-secondary" style="display: block; text-align: center; padding: 12px 16px; margin-top: 1rem; border: 2px solid #e5e7eb; border-radius: 10px; color: #6366f1; text-decoration: none; font-weight: 600; transition: all 0.3s ease; cursor: pointer;">
                    {% trans "Cancel" %}
                </a>
            </form>
        </div>
    </div>
</div>

<style>
    .btn-secondary {
        background: transparent;
        color: #6366f1;
        border: 2px solid #e5e7eb;
        border-radius: 10px;
        padding: 12px 16px;
        text-align: center;
        font-weight: 600;
        transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
        cursor: pointer;
        display: block;
        width: 100%;
        text-decoration: none;
    }

    .btn-secondary:hover {
        background: rgba(99, 102, 241, 0.05);
        border-color: #6366f1;
        color: #4f46e5;
        transform: translateY(-2px);
        box-shadow: 0 4px 12px rgba(99, 102, 241, 0.2);
    }

    .alert-info svg {
        display: inline;
        vertical-align: middle;
    }
</style>
{% endblock %}

