{% block main_class %}p-0{% endblock %}
{% block container_class %}p-0{% endblock %}

{% block extra_css %}
    <link rel="stylesheet" href="{% static 'css/auth.css' %}">
{% endblock %}

{% block content %}
<div class="auth-container">
    <div class="auth-hero">
        <div class="hero-content">
            <h1 class="brand-title">{% trans "Almost There" %}</h1>
            <p class="brand-subtitle">{% trans "Complete your registration to join MunTech and unlock the full power of school management." %}</p>
        </div>
    </div>

    <div class="auth-form-wrapper">
        <div class="glass-card">
            <div class="card-header">
                <h2>{% trans "Complete Sign Up" %}</h2>
                <p>{% blocktrans with provider.name as provider %}Using {{ provider }} account{% endblocktrans %}</p>
            </div>

            <form class="signup login-form" id="signup_form" method="post" action="{% url 'socialaccount_signup' %}">
                {% csrf_token %}

                {% if form.errors %}
                <div class="alert alert-error" style="background: #fee; color: #dc3545; border: 1px solid #fcc; border-radius: 10px; padding: 12px 16px; margin-bottom: 1.5rem; font-size: 0.9rem;">
                    <strong>{% trans "Please fix the errors below:" %}</strong>
                </div>
                {% endif %}

                {% for field in form %}
                    <div class="input-group">
                        {{ field }}
                        {% if field.label %}
                            <label for="{{ field.id_for_label }}">{{ field.label }}</label>
                        {% endif %}
                        {% if field.errors %}
                            <small class="error-text" style="display: block; color: #dc3545; font-size: 0.8rem; margin-top: 4px;">{{ field.errors.0 }}</small>
                        {% endif %}
                        {% if field.help_text %}
                            <small class="form-text text-muted" style="display: block; font-size: 0.75rem; color: #6b7280; margin-top: 4px;">{{ field.help_text|safe }}</small>
                        {% endif %}
                    </div>
                {% endfor %}

                <button type="submit" class="btn-primary" style="margin-top: 1.5rem; text-transform: uppercase; font-weight: 700; letter-spacing: 0.5px;">
                    {% trans "Complete Sign Up" %}
                </button>
            </form>

            <div class="card-footer">
                <p>{% trans "Already have an account?" %} <a href="{% url 'account_login' %}" style="color: #6366f1; text-decoration: none; font-weight: 600;">{% trans "Sign in instead" %}</a></p>
            </div>
        </div>
    </div>
</div>

<style>
    .alert-error {
        background: #fee;
        color: #dc3545;
        border: 1px solid #fcc;
        border-radius: 10px;
        padding: 12px 16px;
        margin-bottom: 1.5rem;
        font-size: 0.9rem;
    }

    .alert-error strong {
        display: block;
        margin-bottom: 4px;
    }

    /* Fix form field styling */
    .input-group input[type="email"],
    .input-group input[type="text"],
    .input-group input[type="username"] {
        width: 100%;
        padding: 14px 16px;
        border: 2px solid #e5e7eb;
        border-radius: 10px;
        font-size: 1rem;
        outline: none;
        transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
        background: rgba(255, 255, 255, 0.8);
        color: #1f2937;
        font-weight: 500;
    }

    .input-group input[type="email"]::placeholder,
    .input-group input[type="text"]::placeholder,
    .input-group input[type="username"]::placeholder {
        color: transparent;
    }

    .input-group input[type="email"]:focus,
    .input-group input[type="text"]:focus,
    .input-group input[type="username"]:focus {
        border-color: #6366f1;
        background: #ffffff;
        box-shadow: 0 0 0 4px rgba(99, 102, 241, 0.1);
    }

    .input-group label {
        position: absolute;
        left: 16px;
        top: 14px;
        color: #6b7280;
        pointer-events: none;
        transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
        background: transparent;
        font-weight: 600;
        font-size: 0.95rem;
    }

    .input-group input[type="email"]:focus ~ label,
    .input-group input[type="email"]:not(:placeholder-shown) ~ label,
    .input-group input[type="text"]:focus ~ label,
    .input-group input[type="text"]:not(:placeholder-shown) ~ label,
    .input-group input[type="username"]:focus ~ label,
    .input-group input[type="username"]:not(:placeholder-shown) ~ label {
        transform: translateY(-26px) scale(0.8);
        left: 12px;
        color: #6366f1;
        background: #ffffff;
        padding: 0 6px;
    }
</style>
{% endblock %}

